function getFeeGrowthInside( mapping(int24 => Tick.Info) storage self, int24 lowerTick_, int24 upperTick_, int24 currentTick, uint256 feeGrowthGlobal0X128, uint256 feeGrowthGlobal1X128
) internal view returns (uint256 feeGrowthInside0X128, uint256 feeGrowthInside1X128)
{ Tick.Info storage lowerTick = self[lowerTick_]; Tick.Info storage upperTick = self[upperTick_]; uint256 feeGrowthBelow0X128; uint256 feeGrowthBelow1X128; if (currentTick >= lowerTick_) { feeGrowthBelow0X128 = lowerTick.feeGrowthOutside0X128; feeGrowthBelow1X128 = lowerTick.feeGrowthOutside1X128; } else { feeGrowthBelow0X128 = feeGrowthGlobal0X128 - lowerTick.feeGrowthOutside0X128; feeGrowthBelow1X128 = feeGrowthGlobal1X128 - lowerTick.feeGrowthOutside1X128; } uint256 feeGrowthAbove0X128; uint256 feeGrowthAbove1X128; if (currentTick < upperTick_) { feeGrowthAbove0X128 = upperTick.feeGrowthOutside0X128; feeGrowthAbove1X128 = upperTick.feeGrowthOutside1X128; } else { feeGrowthAbove0X128 = feeGrowthGlobal0X128 - upperTick.feeGrowthOutside0X128; feeGrowthAbove1X128 = feeGrowthGlobal1X128 - upperTick.feeGrowthOutside1X128; } feeGrowthInside0X128 = feeGrowthGlobal0X128 - feeGrowthBelow0X128 - feeGrowthAbove0X128; feeGrowthInside1X128 = feeGrowthGlobal1X128 - feeGrowthBelow1X128 - feeGrowthAbove1X128;
} 在这里，我们计算两个tick之间（价格范围内）累积的费用。为此，我们首先计算下限tick以下累积的费用，然后计算上限tick以上累积的费用。最后，我们从全局累积的费用中减去这些费用。这就是我们之前看到的公式： fr​=fg​−fb​(il​)−fa​(iu​) 在计算tick上方和下方收集的费用时，我们根据价格范围是否被使用（当前价格是否在价格范围的边界tick之间）采用不同的方法。当它被使用时，我们简单地使用tick的当前费用跟踪器；当它未被使用时，我们需要使用tick的更新后的费用跟踪器——你可以在上面代码的两个else分支中看到这些计算。 在找到头寸内累积的费用后，我们就可以更新头寸的费用和代币数量跟踪器了： // src/lib/Position.sol
function update( Info storage self, int128 liquidityDelta, uint256 feeGrowthInside0X128, uint256 feeGrowthInside1X128
) internal { uint128 tokensOwed0 = uint128( PRBMath.mulDiv( feeGrowthInside0X128 - self.feeGrowthInside0LastX128, self.liquidity, FixedPoint128.Q128 ) ); uint128 tokensOwed1 = uint128( PRBMath.mulDiv( feeGrowthInside1X128 - self.feeGrowthInside1LastX128, self.liquidity, FixedPoint128.Q128 ) ); self.liquidity = LiquidityMath.addLiquidity( self.liquidity, liquidityDelta ); self.feeGrowthInside0LastX128 = feeGrowthInside0X128; self.feeGrowthInside1LastX128 = feeGrowthInside1X128; if (tokensOwed0 > 0 || tokensOwed1 > 0) { self.tokensOwed0 += tokensOwed0; self.tokensOwed1 += tokensOwed1; }
} 在计算欠付的代币时，我们将头寸累积的费用乘以流动性——这与我们在交换过程中所做的相反。最后，我们更新费用跟踪器，并将代币数量添加到先前跟踪的数量中。 现在，每当修改头寸（在添加或移除流动性期间），我们都会计算头寸收集的费用并更新头寸： // src/UniswapV3Pool.sol
function mint(...) { ... bool flippedLower = ticks.update(params.lowerTick, ...); bool flippedUpper = ticks.update(params.upperTick, ...); ... (uint256 feeGrowthInside0X128, uint256 feeGrowthInside1X128) = ticks .getFeeGrowthInside( params.lowerTick, params.upperTick, slot0_.tick, feeGrowthGlobal0X128_, feeGrowthGlobal1X128_ ); position.update( params.liquidityDelta, feeGrowthInside0X128, feeGrowthInside1X128 ); ...
}

